import React, { useState, useEffect } from 'react';
import axios from 'axios';
import moment from 'moment';
import '../css/Transaction.css'

console.log("this run");

export const AddTransaction = () => {
    const [name, setName] = useState('');
    const [number, setInterval] = useState('');
    const [date, setdate] = useState('');
    const [companyname, setCompanyName] = useState('');
    const [email, setEmail] = useState('');
    const [product, setProduct] = useState('');
    const [location, setLocation] = useState('');
    const handleSubmit = async (event) => {
        event.preventDefault();
        const formatted = moment(date).format('MM-DD-YYYY');
        try {
            const response = await axios.post('http://localhost:8080/transactions/newtransactions', {
                textTitle: name,
                amountEnter: number,
                exchangeDate: formatted,
                company: {
                    companyname: companyname,
                    email: email,
                },
                spent: {
                    textTitle: product,
                    location: location,
                }
            });
            console.log('Server response:', response.data);
        } catch (error) {
            console.error('Error sending POST request:', error);
        }
    };
    return (
        <div>
            <md-button class="md-fab md-fab-bottom-right" aria-label="Add a category">
                <md-icon>add</md-icon>
            </md-button>

            <h2>new</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Number"
                    value={number}
                    onChange={(e) => setInterval(e.target.value)}
                />
                <input
                    type="date"
                    placeholder="Date"
                    value={date}
                    onChange={(e) => setdate(e.target.value)}
                />

                <input
                    type="text"
                    placeholder="Company Name"
                    value={companyname}
                    onChange={(e) => setCompanyName(e.target.value)}
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Product"
                    value={product}
                    onChange={(e) => setProduct(e.target.value)}
                />
                <select
                    id="location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                >
                    <option value="none">None</option>
                    <option value="online">Online</option>
                    <option value="inperson">In-Person</option>
                    <option value="subscription">Subscription</option>
                    <option value="other">Other</option>
                </select>
                {location && (
                    <p>You selected: {location}</p>
                )}
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};
//GET
export const GetTransaction = () => {
    const [data, setData] = useState([]);
    // run every 5 seconds
    useEffect(() => {
        const interval = setInterval(() => {
            axios.get(`http://localhost:8080/transactions/gettransactions`)
                .then(res => setData(res.data))
                .catch(err => console.error(err));
        }, 5000);

        return () => clearInterval(interval);
    }, []);

    return (
        <div>
            <h1>Table</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Date</th>
                        <th>Company</th>
                        <th>Email</th>
                        <th>Product</th>
                        <th>location</th>
                        <th>Amount</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item) => (
                        <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.textTitle}</td>
                            <td>{item.exchangeDate}</td>
                            <td>{item.company.companyname}</td>
                            <td>{item.company.email}</td>
                            <td>{item.spent.textTitle}</td>
                            <td>{item.spent.location}</td>
                            <td><h3>${item.amountEnter}</h3></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div >
    );
}
//PUT
export const EditTransaction = () => {
    const [id, setid] = useState('');
    const [name, setName] = useState('');
    const [number, setInterval] = useState('');
    const [date, setdate] = useState('');
    const [companyname, setCompanyName] = useState('');
    const [email, setEmail] = useState('');
    const [product, setProduct] = useState('');
    const [location, setLocation] = useState('');
